/***************************************oracle fix ( change field from clob to varchar2(4000)***************************************/
ALTER TABLE USER_ATTRIBUTES ADD VALUE_CHAR VARCHAR2(4000);

DECLARE
 v_v varchar2(4000);
 CURSOR curl is SELECT ID, VALUE FROM USER_ATTRIBUTES WHERE VALUE IS NOT NULL;
BEGIN 
  FOR get_cur IN curl LOOP
    v_v := get_cur.VALUE;
    UPDATE USER_ATTRIBUTES SET VALUE_CHAR=v_v WHERE ID=get_cur.ID;				
	END LOOP;			
END;
/

ALTER TABLE USER_ATTRIBUTES DROP COLUMN VALUE;
ALTER TABLE USER_ATTRIBUTES RENAME COLUMN VALUE_CHAR TO VALUE;


