DECLARE @xrefId INT = 1;
DECLARE @fieldId NVARCHAR(32);
DECLARE @templateId NVARCHAR(32);
DECLARE cur3 CURSOR FOR (SELECT UI_FIELD_ID, TEMPLATE_ID FROM UI_FIELD_TEMPLATE_XREF);
OPEN cur3;

FETCH NEXT FROM cur3 INTO @fieldId, @templateId;
WHILE @@FETCH_STATUS = 0
BEGIN
	IF (@fieldId IS NOT NULL) 
	BEGIN
		IF (@templateId IS NOT NULL) 
		BEGIN
