--  DDL for View USER_PSWD_EXPIRED_YESTERDAY_VW
--------------------------------------------------------

  CREATE OR REPLACE VIEW IAMUSER.USER_PSWD_EXPIRED_YESTERDAY_VW (LOGIN, MANAGED_SYS_ID, USER_ID, EXPIRATION_DATE, FIRST_NAME, LAST_NAME, STATUS) AS 
  select
    l.LOGIN AS LOGIN,
    l.MANAGED_SYS_ID AS MANAGED_SYS_ID,
    l.USER_ID AS USER_ID,
    l.GRACE_PERIOD AS EXPIRATION_DATE,
    u.FIRST_NAME AS FIRST_NAME,
    u.LAST_NAME AS LAST_NAME,
    u.STATUS AS STATUS
  from LOGIN l, USERS u 
    where
    ((l.USER_ID = u.USER_ID)
     and (l.MANAGED_SYS_ID = 0)
     and (
      ((l.GRACE_PERIOD is not null)
       and (cast(l.GRACE_PERIOD as date) = (CURRENT_DATE - 1))
      ) 
      or
      (
        (l.PWD_EXP is not null)
        and (nvl(l.GRACE_PERIOD, NULL) = NULL)
        and (cast(l.PWD_EXP as date) = (CURRENT_DATE - 1))
               
      )     
     )
    );
